import React, { useEffect, useState } from 'react'
import { StyleSheet, View, Button, Text} from 'react-native'
import { getEspisodes } from '../../component/api/rick-and-morty'

const EpisodeDetailScreen = (props) => {
  const [episodes, setEpisodes] = useState([]) 
  const navigation = props.navigation 
 const urls = props.route.params.urls
 async function buscar(urls){
  urls.map(async (url) => {
    const u = url.replace('https://rickandmortyapi.com/api/episode/', '')
    const ep = await getEspisodes(u)
    setEpisodes([
      ...episodes,
      ep,
    ])
  })
}

useEffect(() => {
  buscar(urls)
}, [])

  return (

   <View>

      <View style={styles.characterContainer}>
        <Image style={styles.characterImage} source={{ uri: character.image }} />
        <View >
          <Text>{character.name}</Text>
          <Text>{character.air_date}</Text>
          <Text>{character.episode}</Text>
        </View>
      </View>


  <Button
    onPress={() => { navigation.navigate('MainScreen') }}
    title="CHARACTERS" />

    </View>
  )
}

export default EpisodeDetailScreen
