import React, { useEffect, useState } from 'react'
import { StyleSheet, View, Button, Text, Image, FlatList} from 'react-native'
import { getEpisode } from '../../component/api/rick-and-morty'

const EpisodeDetailScreen = (props) => {
  const [episodes, setEpisodes] = useState([]) 
  const navigation = props.navigation 
 const urls = props.route.params.urls
 async function buscar(list){
  const aux = []
  list.forEach(async (url, i) => {
    const u = url.replace('https://rickandmortyapi.com/api/episode/', '')
    const ep = await getEpisodes(u)
    aux.push(ep.data)
  })
  setEpisodes(aux)
}
useEffect(() => {
  buscar(urls)
}, [])

return (
  <View>
    <View style={styles.characterContainer}>
    <FlatList
      data={episodes}
      renderItem={({ item }) => (
        <View>
          <View style={styles.characterContainer}>
            <Text>{item.name}</Text>
            <Text>{item.air_date}</Text>
            <Text>{item.episode}</Text>
          </View>
          <Button
            title="CHARACTERS"
            onPress={() => navigation.navigate('MainScreen', { characters: item.characters })}
          />
        </View>
      )}
      keyExtractor={(item, index) => index.toString()}
    />
    </View>
  </View>
  ) 

}

const styles = StyleSheet.create({
  mainView: {
    justifyContent: 'center',
    padding: 10,
    backgroundColor: 'white'
  },
  characterContainer: {
    flexDirection: 'column',
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    alignContent: 'flex-start',
    marginVertical: 5
  },
  characterImage: {
    width: 90,
    height: 90,
    marginRight: 10
  },
  textInput: {
    borderWidth: 1,
    borderColor: '#CCCCCC',
    height: 35,
    padding: 5
  },
  marginVertical: {
    marginVertical: 5
  }
})

export default EpisodeDetailScreen
