import { StyleSheet } from "react-native" 

const stylesTheme = StyleSheet.create({
  dark: {
    theme: {
      mainView: {
        flex: 1,
        backgroundColor: 'black',
      },
      text: {
        color: 'white',
      },
    },
    buttonColor: 'gray',
    fontSize: 14,
    fontSize: 16,
    fontSize: 20,
  },
  light: {
    theme: {
      mainView: {
        flex: 1,
        backgroundColor: 'white',
      },
      text: {
        color: 'black',
      },
    },
    buttonColor: 'black',
  },
});

export default stylesTheme;
