import { StyleSheet, Text, View, TextInput, Button} from 'react-native';
import React from "react"
import { useState } from 'react';


const HomeScreen  = () => {

  const [fruits, setFruits] = useState()

  fetch('fruityvice.com/api/fruit/all')
  .then(response => response.json())
  .then(data => {
    setFruits(data);
  })
  .catch(error => {
    // Lida com o erro da requisição
    console.error(error);
  });

    return (
        <View style={styles.mainView}>
          <Text style={styles.textStyle}>Frutas</Text>

          {fruits.map(() => {
            return(
              <View style={styles.containerFruit}>
                <Text> {fruits.name} </Text>
              </View>
            )
          })}
        </View>
      )
}


const styles = StyleSheet.create({
  mainView: {
    padding: 50,
  },
  textStyle: {
    fontSize: 35,
    color: 'black'
  },
    containerFruit: {
    borderWidth: 1,
    borderRadius: 10,
    borderColor: '#CCCCCC',
    backgroundColor: 'white',
    height: '100%',
    paddingHorizontal: 15,
    marginVertical: 10
  }
})


  export default HomeScreen
