import React, { useContext } from 'react'
import { StyleSheet, View, Button, Text } from 'react-native'
import stylesTheme from '../../styles';
import { UserContext } from '../../contexts/userContext';

const DetailScreen = ({ navigation }) => {

  const { theme } = useContext(UserContext)

  const componentStyle = theme ? stylesTheme[theme] : null;

  if (!componentStyle) {
    return null; 
  }

  useEffect(() => {
    buscar(id);
  }, []);
  async function buscar(id) {
    const c = await getCharacterId(id);
    setCharacter(c.data);
  }

  return (
    <View style={[styles.mainView, componentStyle.theme.mainView]}>
      <Text style={[styles.textTittle, componentStyle.theme.text]}>
        Strawberry
      </Text>
      <View>
        <Text style={[styles.textDescription, componentStyle.theme.text]}>12</Text>
        <Text style={[styles.textDescription, componentStyle.theme.text]}>eae</Text>
        <Text style={[styles.textDescription, componentStyle.theme.text]}>fdsff</Text>
        <Text style={[styles.textDescription, componentStyle.theme.text]}>32141241f</Text>
        <Text style={[styles.textDescription, componentStyle.theme.text]}>fddfsfsdfds</Text>
      </View>
    </View>
  )
}

const styles = StyleSheet.create({
  mainView: {
    justifyContent: 'center',
    padding: 50,
  },
  textTittle: {
    fontSize: 35,
    marginBottom: 20,
  },
  textDescription: {
    fontSize: 20,
  }
})

export default DetailScreen
