import React, { useContext, useEffect, useState } from 'react'
import { StyleSheet, View, Text } from 'react-native'
import stylesTheme from '../../styles';
import { UserContext } from '../../contexts/userContext';
import axios from 'axios';

const DetailScreen = () => {

  const { theme, character } = useContext(UserContext)

  const componentStyle = theme ? stylesTheme[theme] : null;

  const [person, setPerson] = useState({})

  if (!componentStyle) {
    return null; 
  }

  async function CharacterLoad() {
    axios.get(`${character}`)
    .then((response) => {
      const results = response.data;
      setPerson(results);
      })
      .catch((error) => {
        console.error(error);
      });
}

async function CharacterLoad() {
  axios.get(`${character}`)
  .then((response) => {
    const results = response.data;
    setPerson(results);
    })
    .catch((error) => {
      console.error(error);
    });
}

useEffect(() => {
  CharacterLoad()
},[character])

  return (
    <View style={[styles.mainView, componentStyle.theme.mainView]}>
      <Text style={[styles.textTittle, componentStyle.theme.text]}>
        Personagem
      </Text>
      {person.name ? (
      <View>
        <Text style={[styles.textDescription, componentStyle.theme.text]}>{person.name}</Text>
        <Text style={[styles.textDescription, componentStyle.theme.text]}>{person.mass}</Text>
        <Text style={[styles.textDescription, componentStyle.theme.text]}>{person.height}</Text>
        <Text style={[styles.textDescription, componentStyle.theme.text]}>{person.gender}</Text>
        <Text style={[styles.textDescription, componentStyle.theme.text]}>{person.hair_color}</Text>
      </View>
    ) : (
      <Text>Loading...</Text>
    )}
    </View>
  )
}

const styles = StyleSheet.create({
  mainView: {
    justifyContent: 'center',
    padding: 50,
  },
  textTittle: {
    fontSize: 35,
    marginBottom: 20,
  },
  textDescription: {
    fontSize: 20,
  }
})

export default DetailScreen
