import React, { useEffect, useState } from "react";
import { StyleSheet, View, Button, Text } from "react-native";
import { getLocation } from "../../component/api/rick-and-morty";
import UserScreen from "../userScreen";
import { useContext } from "react";
import { UserContext } from "../../contexts/userContext";
import { stylesTheme } from "../../styles";

const LocationDetailScreen = (props) => {
  const [location, setLocation] = useState([]);
  const navigation = props.navigation;
  const url = props.route.params.url;
  async function buscar() {
    const u = url.replace("https://rickandmortyapi.com/api/location/", "");
    const loc = await getLocation(u);
    setLocation(loc.data);
  }
  useEffect(() => {
    buscar();
  }, []);

  const { theme } = useContext(UserContext)

    const componentStyle = theme ? stylesTheme[theme] : null;

  if (!componentStyle) {
    return null; 
  }

  return (
    <View style={[componentStyle.theme.mainView, styles.mainView]}>
      <UserScreen />
      <View style={styles.characterContainer}>
        <View>
          <Text style={componentStyle.theme.text}>{location.name}</Text>
          <Text style={componentStyle.theme.text}>{location.type}</Text>
          <Text style={componentStyle.theme.text}>{location.dimension}</Text>
        </View>
      </View>

      <Button
        color={componentStyle.buttonColor}
        onPress={() => {
          navigation.navigate("Main", {characters: location.residents});
        }}
        title="RESIDENTS"
      />
    </View>
  );
};

const styles = StyleSheet.create({
  mainView: {
    justifyContent: "center",
    padding: 10,
    backgroundColor: "white",
  },
  characterContainer: {
    flexDirection: "column",
    alignItems: "flex-start",
    justifyContent: "flex-start",
    alignContent: "flex-start",
    marginVertical: 5,
  },
  characterImage: {
    width: 90,
    height: 90,
    marginRight: 10,
  },
  textInput: {
    borderWidth: 1,
    borderColor: "#CCCCCC",
    height: 35,
    padding: 5,
  },
  marginVertical: {
    marginVertical: 5,
  },
});

export default LocationDetailScreen;
