import React, { useEffect, useState } from 'react'
import { StyleSheet, View, Button, Text} from 'react-native'
import { getEspisodes } from '../../component/api/rick-and-morty'

const EpisodeDetailScreen = (props) => {
  const [episodes, setEpisodes] = useState([]) 
  const navigation = props.navigation 
 const urls = props.route.params.urls
 async function buscar(list){
  list.map(async (url) => {
    const u = url.replace('https://rickandmortyapi.com/api/episode/', '')
    const ep = await getEspisodes(u)
    setEpisodes([
      ...episodes,
      ep,
    ])
  })
}
useEffect(() => {
  buscar(urls)
}, [])

  return (

   <View>

          episodes.map(episode => (
        <View>
          <Image source={{ uri: episode.url }} />
          <Text>{episode.name}</Text>
          <Text>{episode.air_date}</Text>
          <Text>{episode.episode}</Text>
          <Button
            title="characters"
            onPress={() => navigation.navigate('MainScreen', { characters: episode.characters })}
          />
        </View>
      )) 

    </View>
  )
}

export default EpisodeDetailScreen
