import { useContext, createContext } from "react";
import { View, Text } from "react-native";

export const UserContext = createContext({name:'', setUser: null})

const UserScreen = () => {
    const user = useContext(UserContext)

    return (
        <View style={{margin:5}}>
          <Text> {user.name} </Text>
        </View>
    )
  }

  export default UserScreen
