import React, { useContext } from 'react'
import { StyleSheet, View, Button, Text } from 'react-native'
import stylesTheme from '../../styles';
import { UserContext } from '../../contexts/userContext';


const favoriteScreen = () => {

  const { theme, favorite } = useContext(UserContext)

  const componentStyle = theme ? stylesTheme[theme] : null;

  if (!componentStyle) {
    return null; 
  }

  return (
    <View style={[componentStyle.theme.mainView,]}>
      <Text style={[componentStyle.theme.text,]}> Favorites </Text>

      {favorite.map((fav)=> {
        <View>
        <Text style={[componentStyle.theme.text,]}> Strawberry </Text>
        <Button> Retirar dos favoritos </Button>
      </View>
      })}
    </View>
  )
}

const styles = StyleSheet.create({
  mainView: {
    justifyContent: 'center',
    padding: 10
  },
  buttonView: {
    marginVertical: 2
  }
})

export default favoriteScreen
