import React, { useContext, useEffect, useState } from 'react'
import { StyleSheet, View, Text } from 'react-native'
import stylesTheme from '../../styles';
import { UserContext } from '../../contexts/userContext';

const DetailScreen = () => {

  const { theme, character } = useContext(UserContext)

  const componentStyle = theme ? stylesTheme[theme] : null;

  if (!componentStyle) {
    return null; 
  }

  return (
    <View style={[styles.mainView, componentStyle.theme.mainView]}>
      <Text style={[styles.textTittle, componentStyle.theme.text]}>
        Strawberry
      </Text>
      <View>
        <Text style={[styles.textDescription, componentStyle.theme.text]}>{character.name}</Text>
        <Text style={[styles.textDescription, componentStyle.theme.text]}>eae</Text>
        <Text style={[styles.textDescription, componentStyle.theme.text]}>fdsff</Text>
        <Text style={[styles.textDescription, componentStyle.theme.text]}>32141241f</Text>
        <Text style={[styles.textDescription, componentStyle.theme.text]}>fddfsfsdfds</Text>
      </View>
    </View>
  )
}

const styles = StyleSheet.create({
  mainView: {
    justifyContent: 'center',
    padding: 50,
  },
  textTittle: {
    fontSize: 35,
    marginBottom: 20,
  },
  textDescription: {
    fontSize: 20,
  }
})

export default DetailScreen
