import axios from "axios";

const baseURL = "https://rickandmortyapi.com/api/";

const client = axios.create({
  baseURL: baseURL,
  timeout: 5000,
});

const getNextCharacterPage = async (url) => {
  return await client.get(url);
};

const getCharacter = async ({ name = "" }) => {
  const uri = encodeURI(`character/?name=${name}`);
  return await client.get(uri);
};

const getCharacterId = async (id) => {
  const uri = encodeURI(`character/${id}`);
  return await client.get(uri);
};

const getEpisode = async (id) => {
  const uri = encodeURI(`episode/${id}`);
  return await client.get(uri);
};

const getLocation = async (id) => {
  const uri = encodeURI(`location/${id}`)
  return await client.get(uri)
};

const getCharactersId = async (ids=[]) => {
  const uri = encodeURI(`character/${ids.toString()}`);
  return await client.get(uri);
};

export { getCharacter, getNextCharacterPage, getCharacterId, getEpisode, getLocation, getCharactersId };
