import React, { useContext } from 'react'
import { StyleSheet, View, TextInput, Button, Text } from 'react-native';
import stylesTheme from '../../styles';
import { UserContext } from '../../contexts/userContext';
import { useState } from 'react';

const SearchScreen = (props) => {

  const navigation = props.navigation

  const { theme, characters, HandleSearch } = useContext(UserContext)

  const [searchValue, setSearchValue] = useState();
  const [searchResult, setSearchResult] = useState([]);

  const componentStyle = theme ? stylesTheme[theme] : null;

  if (!componentStyle) {
    return null; 
  }

  const getCharacterByName = (name) => {
    const lowercaseName = name.toLowerCase();
    const results = characters.filter((character) =>
      character.name.toLowerCase().includes(lowercaseName)
    );
    setSearchResult(results);
  };

  return (
    <View style={[styles.mainView, componentStyle.theme.mainView]}>
      <TextInput 
        onChangeText={(text) => setSearchValue(text)} 
        style={[styles.textInput, componentStyle.theme.text]} 
        placeholder='O que você quer?' 
      />

      <Button
        color={componentStyle.buttonColor} 
        title="Buscar" onPress={() => getCharacterByName(searchValue)} 
      />

      {searchResult.length > 0 ? (
        searchResult.map((character) => (
          <View style={styles.resultMain} key={character.name}>
            <Text>Personagem encontrado:</Text>
            <Text>Nome: {character.name}</Text>
            <Text>Idade: {character.birth_year}</Text>
            <Button 
              color={componentStyle.buttonColor} 
              style={styles.containerCard} 
              onPress={() => {return (HandleSearch(character.url), navigation.navigate('detailScreen'))}} 
              title='detalhes do personagem' 
            />
          </View>
        ))
      ) : (
        <Text>Nenhum personagem encontrado.</Text>
      )}
    </View>
  )
}

const styles = StyleSheet.create({
  textInput: {
    borderWidth: 1,
    borderColor: '#CCCCCC',
    height: 35,
    padding: 5,
    marginBottom: 30,
  },
  mainView: {
    padding: 50,
  },
  resultMain: {
    marginTop: 100,
    padding: 50,
  },
})

export default SearchScreen
