import React, { useEffect, useState } from 'react'
import { StyleSheet, Button, View, Image, Text, FlatList, TextInput, TouchableOpacity} from 'react-native'
import { getCharacter, getCharactersId, getNextCharacterPage } from '../../component/api/rick-and-morty'

const MainScreen = (props) => {
  const [characters, setCharacters] = useState([])
  const [pageInfo, setPageInfo] = useState({})
  const [nameSearch, setNameSearch] = useState(' ')
  const navigation = props.navigation
  useEffect(async () => {
    const c = props.route.params.characters;
    const ch = c.map(ch => ch.split('/').pop())
    const u = await getCharactersId(ch);
    console.log(u)
    setCharacters(u.data)
  }, [props.route.params]);

  return (
    <View style={styles.mainView}>
      <TextInput
        style={[styles.textInput, styles.marginVertical]}
        onChangeText={setNameSearch}
        value={nameSearch}
      />
      <Button
        title="Load Characters"
        onPress={async () => {
          try {
            const chars = await getCharacter({
              name: nameSearch
            })

            setPageInfo(chars.data.info)
            setCharacters(chars.data.results)
          } catch (error) {
            setPageInfo({})
            setCharacters([])
          }
        }}
      />


      <FlatList
        style={styles.marginVertical}
        data={characters}
        keyExtractor={(item) => item.id}
        renderItem={(element) => {
          const character = element.item

          return (
            <TouchableOpacity
            onPress={() => {
              console.log('touchable opacity pressed')
              navigation.navigate('CharDetail', {id:character.id})
            }}>

            <View style={styles.characterContainer}>
              <Image style={styles.characterImage} source={{ uri: character.image }} />
              <View >
                <Text>{character.name}</Text>
                <Text>{character.status}</Text>
              </View>
            </View>
            </TouchableOpacity>
          )

        }}
      /> 

      <Button
        title="Load More..."
        disabled={!pageInfo.next}
        onPress={async () => {
          const chars = await getNextCharacterPage(pageInfo.next)

          setPageInfo(chars.data.info)
          setCharacters([...characters, ...chars.data.results])
        }}
      />
    </View>
  )
}

const styles = StyleSheet.create({
  mainView: {
    justifyContent: 'center',
    padding: 10,
    backgroundColor: 'white'
  },
  characterContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    marginVertical: 5
  },
  characterImage: {
    width: 90,
    height: 90,
    marginRight: 10
  },
  textInput: {
    borderWidth: 1,
    borderColor: '#CCCCCC',
    height: 35,
    padding: 5
  },
  marginVertical: {
    marginVertical: 5
  }
})

export default MainScreen
