import { StyleSheet, Text, View, TextInput, Button} from 'react-native';
import React, { useContext } from "react"
import { useState } from 'react';
import { api } from '../../lib/axios'; 
import { useEffect } from 'react';
import stylesTheme from '../../styles';
import { UserContext } from '../../contexts/userContext';

const HomeScreen  = (props) => {

  const navigation = props.navigation

  const { theme } = useContext(UserContext)

  const componentStyle = theme ? stylesTheme[theme] : null;

  if (!componentStyle) {
    return null; 
  }

  const [cards, setCards] = useState([])

  async function CardsLoad() {
    api.get(`/api/v7/cardinfo.php`)
    .then((response) => {
      const { data } = response.data;
      setCards(data);
      })
      .catch((error) => {
        console.error(error);
      });
}

useEffect(() => {
  CardsLoad()
},[])

    return (
        <View style={[styles.mainView, componentStyle.theme.mainView]}>
          <Text style={[styles.textStyle, componentStyle.theme.text]}>Frutas</Text>

          <View style={styles.containerFruit}>
            <Text onPress={() => {
              navigation.navigate('detailScreen')             
            }}> Detail </Text>
          </View>

          <View style={styles.containerFruit}>
            <Text onPress={() => {
              navigation.navigate('favoriteScreen')             
            }}> favoriteScreen </Text>
          </View>
          <View style={styles.containerFruit}>
            <Text onPress={() => {
              navigation.navigate('settingScreen')             
            }}> settingScreen </Text>
          </View>

          <View style={styles.containerFruit}>
            <Text onPress={() => {
              navigation.navigate('searchScreen')             
            }}> searchScreen </Text>
          </View>

          {cards.map((card) => {
            const {
              name,
              desc,
              card_images: [{ image_url }]
            } = card;

            return (
              <View style={styles.containerFruit} key={card.id}>
                <Text>{name}</Text>
                <Text>{desc}</Text>
                <Image source={{ uri: image_url }} style={styles.cardImage} />
              </View>
            );
          })}

        </View>
      )
}

const styles = StyleSheet.create({
  mainView: {
    padding: 50,
  },
  textStyle: {
    fontSize: 35,
    color: 'black'
  },
    containerFruit: {
    borderWidth: 1,
    borderRadius: 10,
    borderColor: '#CCCCCC',
    backgroundColor: 'white',
    height: 100,
    paddingHorizontal: 15,
    marginVertical: 10,
    alignItems: 'center',
    justifyContent: 'center',
  }
})


  export default HomeScreen
