import React, { useContext, useEffect, useState } from "react";
import { StyleSheet, View, Button, Text, Image, FlatList } from "react-native";
import { getEpisode } from "../../component/api/rick-and-morty";
import UserScreen from "../userScreen";
import { stylesTheme } from "../../styles";
import { UserContext } from "../../contexts/userContext";

const EpisodeDetailScreen = (props) => {
  const [episodes, setEpisodes] = useState([]);
  const navigation = props.navigation;
  const urls = props.route.params.urls;
  async function buscar(list) {
    const aux = [];
    for (const url of list) {
      const u = url.replace("https://rickandmortyapi.com/api/episode/", "");
      const ep = await getEpisode(u);
      aux.push(ep.data);
    };
    setEpisodes(aux);
  }
  useEffect(() => {
    buscar(urls);
  }, []);

  const { theme } = useContext(UserContext)

    const componentStyle = theme ? stylesTheme[theme] : null;

  if (!componentStyle) {
    return null; 
  }

  return (
    <View style={[componentStyle.theme.mainView, styles.mainView]}>
      <UserScreen />
      <View style={[styles.characterContainer]}>
        <FlatList
          data={episodes}
          renderItem={({ item }) => (
            <View>
              <View style={styles.characterContainer}>
                <Text style={componentStyle.theme.text}>{item.name}</Text>
                <Text style={componentStyle.theme.text}>{item.air_date}</Text>
                <Text style={componentStyle.theme.text}>{item.episode}</Text>
              </View>
              <Button
                color={componentStyle.buttonColor}
                title="CHARACTERS"
                onPress={() =>
                  navigation.navigate("Main", {
                    characters: item.characters,
                  })
                }
              />
            </View>
          )}
          keyExtractor={(item, index) => index.toString()}
        />
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  mainView: {
    justifyContent: "center",
    padding: 50,
  },
  characterContainer: {
    flexDirection: "column",
    alignItems: "flex-start",
    justifyContent: "flex-start",
    alignContent: "flex-start",
    marginVertical: 5,
  },
  characterImage: {
    width: 90,
    height: 90,
    marginRight: 10,
  },
  textInput: {
    borderWidth: 1,
    borderColor: "#CCCCCC",
    height: 35,
    padding: 5,
  },
  marginVertical: {
    marginVertical: 5,
  },
});

export default EpisodeDetailScreen;
