import axios from "axios";
import { useEffect, useState } from "react";
import { createContext } from "react";
import AsyncStorage from '@react-native-async-storage/async-storage';

export const UserContext = createContext({setUser: null})

export const UserProvider = ({ children }) => {

  const [theme, setThemeStyle] = useState('light')

  const [character, setCharacter] = useState();

  const [favorite, setFavorite] = useState([]);

  const [active, setActive] = useState({});

  const [characters, setCharacters] = useState([])

  const [person, setPerson] = useState({})


  async function PersoLoad() {
    axios.get(`https://swapi.dev/api/people/`)
    .then((response) => {
      const results = response.data.results;
      setCharacters(results);
      })
      .catch((error) => {
        console.error(error);
      });
}

  function SearchPerson(url) {
    setPerson(url)
  } 

  function HandleSearch (url) {
    setCharacter(url)
  }

  function ButtonChangeTheme () {
        setThemeStyle((currentTheme) => currentTheme === 'light' ? 'dark' : 'light')
        handleThemeSettingsAsyncStorage()
  }

  async function handleFavoriteAsyncStorage() {
    try {
      await AsyncStorage.setItem("@Favorite1", JSON.stringify(favorite));
    } catch (error) {
      console.error(error);
    }
  }

  async function getDataFavorite() {
    try {
      const response = await AsyncStorage.getItem("@Favorite1");
      if (response) {
        setFavorite(JSON.parse(response));
      }
    } catch (error) {
      console.error(error);
    }
  }

  async function handleThemeSettingsAsyncStorage() {
    try {
      await AsyncStorage.setItem("@Theme1", theme);
    } catch (error) {
      console.error(error);
    }
  }

  async function getDataThemeSettings() {
      const response = await AsyncStorage.getItem("@Theme1");
      if (response) {
        setTheme(response);
      }
} 

  async function HandleFavorite(fav) {
    const index = favorite.findIndex((item) => item.name === fav.name);

    if (index === -1) {
      setFavorite([...favorite, fav]);
      setActive((prevState) => ({ ...prevState, [fav.name]: true }));
    } else {
      const updatedFavorites = [...favorite];
      updatedFavorites.splice(index, 1);
      setFavorite(updatedFavorites);
      setActive((prevState) => {
        const updatedActive = { ...prevState };
        delete updatedActive[fav.name];
        return updatedActive;
      });
    }

    handleFavoriteAsyncStorage();
  }

  useEffect(() => {
    PersoLoad()
    getDataFavorite()
    getDataThemeSettings()
  },[])

  const userContextValue = {
    character,
    theme,
    ButtonChangeTheme,
    HandleSearch,
    favorite,
    HandleFavorite,
    active,
    characters,
    person,
    SearchPerson,
    getDataFavorite
  };

  return (
    <UserContext.Provider  value={userContextValue}>
      {children}
    </UserContext.Provider>
  );
};
