import { StyleSheet } from "react-native" 

const stylesTheme = StyleSheet.create({

  dark: {
    theme: {
      mainView: {
        flex: 1,
        backgroundColor: 'black'
      },
      text: {
        color: 'white'
      },
    },
    buttonColor: 'gray',
  },
  light: {
    theme: {
      mainView: {
        flex: 1,
        backgroundColor: 'white'
      }
    },
    text: {
      text: {
        color: 'black'
      }
    },
    buttonColor: 'black',
  },
  fontSmall: {
    fontSize: 14,
  },
  fontDefault: {
    fontSize: 16,
  },
  fontGiant: {
    fontSize: 20,
  },
})

export default stylesTheme
