import { useContext } from "react";
import { StyleSheet, View, Text, CheckBox } from "react-native";
import { stylesTheme } from "../../styles";
import { UserContext } from "../../contexts/userContext";

const ThemeScreen = () => {

  const { theme, ButtonChangeTheme } = useContext(UserContext)

  const componentStyle = theme ? stylesTheme[theme] : null;

  if (!componentStyle) {
    return null; 
  }
  return (
    <>
      <View style={[componentStyle.theme.mainView, styles.checkBoxComponent]}>
        <CheckBox active color={componentStyle.buttonColor} onPress={() => ButtonChangeTheme()}/>
        <Text style={componentStyle.theme.text}> Toggle dark/light mode </Text>
      </View>
    </>
  )
}

const styles = StyleSheet.create({
    checkBoxComponent:{
    display: 'flex',
  }
})


export default ThemeScreen
