import { Button, StyleSheet, Text, View } from 'react-native';
import React, { useContext } from "react"
import { useState } from 'react';
import { useEffect } from 'react';
import stylesTheme from '../../styles';
import { UserContext } from '../../contexts/userContext';
import axios from 'axios';
import { Heart } from '@phosphor-icons/react';

const HomeScreen  = (props) => {

  const navigation = props.navigation

  const { theme, HandleSearch } = useContext(UserContext)

  const [characters, setCharacters] = useState([])

  const componentStyle = theme ? stylesTheme[theme] : null;

  if (!componentStyle) {
    return null; 
  }

  async function PersoLoad() {
    axios.get(`https://swapi.dev/api/people/`)
    .then((response) => {
      const results = response.data.results;
      setCharacters(results);
      })
      .catch((error) => {
        console.error(error);
      });
}

useEffect(() => {
  PersoLoad()
},[])

    return (
        <View style={[styles.mainView, componentStyle.theme.mainView]}>
          <Text style={[styles.textStyle, componentStyle.theme.text]}>Personagens de star wars</Text>
          {characters ? (
            characters.map((person) => {
              return (
                <View style={styles.viewButton}>
                  <Button color={componentStyle.buttonColor} style={styles.containerCard} key={person.url} onPress={() => {return (HandleSearch(person.url), navigation.navigate('detailScreen'))}} title={person.name} />
                  <Button color={componentStyle.buttonColor} style={styles.viewButton} key={person.url} onPress={() => {return (HandleSearch(person.url), navigation.navigate('detailScreen'))}} title={<Heart size={32} />} />
                </View>
              );
            })
          ) : (
            <Text>Loading...</Text>
          )}
          </View>
        )
}

const styles = StyleSheet.create({
  mainView: {
    padding: 50,
    gap: 10,
    alignItems: 'center',
  },
  textStyle: {
    fontSize: 35,
    color: 'black',
    marginBottom: 100,
  },
    containerCard: {
    cursor: 'pointer',
    padding: 20,
  },
  viewButton: {
    flex: 1,
    flexDirection: 'row',
    width: '100%',
  }
})


  export default HomeScreen
