import React, { useContext } from 'react'
import { StyleSheet, View, TextInput } from 'react-native'
import stylesTheme from '../../styles';
import { UserContext } from '../../contexts/userContext';

const SearchScreen = () => {

  const { theme, characters } = useContext(UserContext)

  const componentStyle = theme ? stylesTheme[theme] : null;

  if (!componentStyle) {
    return null; 
  }

  function getCharacterByName(name) {
    return characters.find(character => character.name === name);
  }

  return (
    <View style={[styles.mainView, componentStyle.theme.mainView]}>
      <TextInput onChange={(e)=> {console.log(e.target.value)}} style={[styles.textInput, componentStyle.theme.text]} placeholder='O que você quer?' />
    </View>
  )
}

const styles = StyleSheet.create({
  textInput: {
    borderWidth: 1,
    borderColor: '#CCCCCC',
    height: 35,
    padding: 5,
  },
  mainView: {
    padding: 50,
  }
})

export default SearchScreen
