import React, { useContext } from 'react'
import { StyleSheet, View, TextInput, Button, Text } from 'react-native';
import stylesTheme from '../../styles';
import { UserContext } from '../../contexts/userContext';
import { useState } from 'react';

const SearchScreen = () => {

  const { theme, characters } = useContext(UserContext)

  const [searchValue, setSearchValue] = useState();
  const [searchResult, setSearchResult] = useState(null);

  const componentStyle = theme ? stylesTheme[theme] : null;

  if (!componentStyle) {
    return null; 
  }

  const getCharacterByName = (name) => {
    const lowercaseName = name.toLowerCase();
    const results = characters.filter((character) =>
      character.name.toLowerCase().includes(lowercaseName)
    );
    setSearchResult(results);
  };

  return (
    <View style={[styles.mainView, componentStyle.theme.mainView]}>
      <TextInput onChange={(e)=> {setSearchValue(e.target.value)}} style={[styles.textInput, componentStyle.theme.text]} placeholder='O que você quer?' />

      <Button title="Buscar" onPress={() => getCharacterByName(searchValue)} />

      {searchResult && (
        <View>
          <Text>Personagem encontrado:</Text>
          <Text>Nome: {searchResult.name}</Text>
          <Text>Idade: {searchResult.birth_year}</Text>
        </View>
      )}
    </View>
  )
}

const styles = StyleSheet.create({
  textInput: {
    borderWidth: 1,
    borderColor: '#CCCCCC',
    height: 35,
    padding: 5,
  },
  mainView: {
    padding: 50,
  }
})

export default SearchScreen
