const SearchScreen = () => {
  const { theme, characters } = useContext(UserContext);
  const [searchValue, setSearchValue] = useState('');
  const [searchResult, setSearchResult] = useState(null);

  const componentStyle = theme ? stylesTheme[theme] : null;

  const getCharacterByName = (name) => {
    const character = characters.find((character) => character.name === name);
    setSearchResult(character);
  };

  return (
    <View style={[styles.mainView, componentStyle.theme.mainView]}>
      <TextInput
        onChangeText={(text) => setSearchValue(text)}
        style={[styles.textInput, componentStyle.theme.text]}
        placeholder='O que você quer?'
      />

      <Button title="Buscar" onPress={() => getCharacterByName(searchValue)} />

      {searchResult && (
        <View>
          <Text>Personagem encontrado:</Text>
          <Text>Nome: {searchResult.name}</Text>
          <Text>Idade: {searchResult.birth_year}</Text>
        </View>
      )}
    </View>
  );
};

export default SearchScreen;
