import React, { useContext, useState } from 'react'
import { StyleSheet, Text, View, Button} from 'react-native'
import ThemeScreen from '../../component/theme'
import stylesTheme from '../../styles'
import { UserContext } from '../../contexts/userContext'

const settingScreen = () => {

  const { theme, fontSize, ButtonChangeFontSmall, ButtonChangeFontDefault, ButtonChangeFontGiant } = useContext(UserContext)

  const componentStyle = theme ? stylesTheme[theme] : null;

  if (!componentStyle) {
    return null; 
  }

  return (
    <View style={[styles.mainView, componentStyle.theme.mainView]}>
      <Text style={[styles.textTittle, componentStyle.theme.text]}>Configurações</Text>
      <View style={styles.buttonsContainer}>
        <Button
          title="Small"
          onPress={() => ButtonChangeFontSmall('fontSmall')}
          style={fontSize === 'fontSmall' ? styles.activeButton : null}
        />
        <Button
          title="Default"
          onPress={() => ButtonChangeFontDefault('fontDefault')}
          style={fontSize === 'fontDefault' ? styles.activeButton : null}
        />
        <Button
          title="Giant"
          onPress={() => ButtonChangeFontGiant('fontGiant')}
          style={fontSize === 'fontGiant' ? styles.activeButton : null}
        />
      </View>
      <ThemeScreen />
    </View>
  )
}

const styles = StyleSheet.create({
  mainView: {
    justifyContent: 'center',
    padding: 50
  },
  textTittle: {
    fontSize: 35,
    marginBottom: 20,
  },
  buttonsContainer: {
    gap: 10,
  }
})

export default settingScreen
