import { useEffect, useState } from "react";
import { createContext } from "react";

export const UserContext = createContext({setUser: null})

export const UserProvider = ({ children }) => {

  const [theme, setThemeStyle] = useState('light')
  const [character, setCharacter] = useState();
  const [favorite, setFavorite] = useState([]);
  const [active, setActive] = useState(true);
  const [characters, setCharacters] = useState([])

  async function PersoLoad() {
    axios.get(`https://swapi.dev/api/people/`)
    .then((response) => {
      const results = response.data.results;
      setCharacters(results);
      })
      .catch((error) => {
        console.error(error);
      });
}

useEffect(() => {
  PersoLoad()
},[])

  function ButtonChangeTheme () {
        setThemeStyle((currentTheme) => currentTheme === 'light' ? 'dark' : 'light')
  }

  function HandleSearch (url) {
    setCharacter(url)
  }

  function HandleFavorite(fav) {
    const index = favorite.findIndex((item) => item.name === fav.name);

  if (index === -1) {
    setFavorite([...favorite, fav]);
  } else {
    const updatedFavorites = [...favorite];
    updatedFavorites.splice(index, 1);
    setFavorite(updatedFavorites);;
  }
  }  

  const userContextValue = {
    character,
    theme,
    ButtonChangeTheme,
    HandleSearch,
    favorite,
    HandleFavorite,
    active,
    characters,
  };

  return (
    <UserContext.Provider  value={userContextValue}>
      {children}
    </UserContext.Provider>
  );
};
