import { Button, StyleSheet, Text, View } from 'react-native';
import React, { useContext } from "react"
import { useState } from 'react';
import { api } from '../../lib/axios'; 
import { useEffect } from 'react';
import stylesTheme from '../../styles';
import { UserContext } from '../../contexts/userContext';

const HomeScreen  = (props) => {

  const navigation = props.navigation

  const { theme } = useContext(UserContext)

  const componentStyle = theme ? stylesTheme[theme] : null;

  if (!componentStyle) {
    return null; 
  }

  const [characters, setCharacters] = useState([])

  async function CardsLoad() {
    api.get(`people`)
    .then((response) => {
      const results = response.data.results;
      setCharacters(results);
      })
      .catch((error) => {
        console.error(error);
      });
}

useEffect(() => {
  CardsLoad()
},[])

    return (
        <View style={[styles.mainView, componentStyle.theme.mainView]}>
          <Text style={[styles.textStyle, componentStyle.theme.text]}>Personagens de star wars</Text>

          <View style={styles.containerFruit}>
            <Text onPress={() => {
              navigation.navigate('detailScreen')             
            }}> Detail </Text>
          </View>

          <View style={styles.containerFruit}>
            <Text onPress={() => {
              navigation.navigate('favoriteScreen')             
            }}> favoriteScreen </Text>
          </View>
          <View style={styles.containerFruit}>
            <Text onPress={() => {
              navigation.navigate('settingScreen')             
            }}> settingScreen </Text>
          </View>

          <View style={styles.containerFruit}>
            <Text onPress={() => {
              navigation.navigate('searchScreen')             
            }}> searchScreen </Text>
          </View>

          {characters ? (
            characters.map((person) => {
              return (
                <Button onPress={() => {navigation.navigate('detailScreen', {id:person.id})}} style={styles.containerCard} title={person.name} />
              );
            })
          ) : (
            <Text>Loading...</Text>
          )}

          </View>
        )
}

const styles = StyleSheet.create({
  mainView: {
    padding: 50,
  },
  textStyle: {
    fontSize: 35,
    color: 'black'
  },
    containerCard: {
    borderWidth: 1,
    borderRadius: 10,
    borderColor: '#CCCCCC',
    backgroundColor: 'white',
    height: 100,
    paddingHorizontal: 15,
    marginVertical: 10,
    alignItems: 'center',
    justifyContent: 'center',
    cursor: 'pointer',
  }
})


  export default HomeScreen
