import React, { useContext, useEffect, useState } from 'react'
import { StyleSheet, View, Text } from 'react-native'
import stylesTheme from '../../styles';
import { UserContext } from '../../contexts/userContext';
import axios from 'axios';

const DetailScreen = () => {

  const { theme, character } = useContext(UserContext)

  const componentStyle = theme ? stylesTheme[theme] : null;

  const [person, setPerson] = useState({})

  const [films, setFilms] = useState([])
  const [homeWorld, setHomeWorld] = useState([])
  const [starShips, setStarShips] = useState([])
  const [species, setSpecies] = useState([])

  if (!componentStyle) {
    return null; 
  }

  async function CharacterLoad() {
    axios.get(`${character}`)
    .then((response) => {
      const results = response.data;
      setPerson(results);
      })
      .catch((error) => {
        console.error(error);
      });
}

async function FilmsLoad() {
  try {
    const filmPromises = person.films.map(filmUrl => axios.get(filmUrl));
    const filmResponses = await Promise.all(filmPromises);
    const films = filmResponses.map(response => response.data);
    setFilms(films);
  } catch (error) {
    console.error(error);
  }
}

async function HomeWorldLoad() {
  try {
    const response = await axios.get(person.homeworld);
    const homeWorldData = response.data;
    setHomeWorld(homeWorldData);
  } catch (error) {
    console.error(error);
  }
}

async function StarShipsLoad() {
  try {
    const starShipsPromises = person.starships.map(startShipsUrl => axios.get(startShipsUrl));
    const starShipsResponses = await Promise.all(starShipsPromises);
    const starShips = starShipsResponses.map(response => response.data);
    setStarShips(starShips);
  } catch (error) {
    console.error(error);
  }
}

async function SpeciesLoad() {
  try {
    if (person.species && person.species.length > 0) {
      const response = await axios.get(person.species[0]);
      const speciesData = response.data;
      setSpecies(speciesData);
    }
  } catch (error) {
    console.error(error);
  }
}

useEffect(() => {
  CharacterLoad()
},[character])

useEffect(() => {
  if (person.films) {
    FilmsLoad();
    HomeWorldLoad();
    StarShipsLoad();
    SpeciesLoad();
  }
}, [person]);

  return (
    <View style={[styles.mainView, componentStyle.theme.mainView]}>
      <Text style={[styles.textTittle, componentStyle.theme.text]}>
        Personagem
      </Text>
      {person.name ? (
      <View>
        <Text style={[styles.textDescription, componentStyle.theme.text]}> Nome: {person.name} </Text>
        <Text style={[styles.textDescription, componentStyle.theme.text]}>Nasceu: {homeWorld == '' ? '' : homeWorld.name} </Text>
        <Text style={[styles.textDescription, componentStyle.theme.text]}>{starShips.length == 0 ? '' : starShips.map((ship) => {ship.name})}</Text>
        <Text style={[styles.textDescription, componentStyle.theme.text]}> Espécie: {species.length == '' ? '' : species.name} </Text>
        <Text style={[styles.textDescription, componentStyle.theme.text]}> Filmes:{species.length == 0 ? '' : films.map((film) => film.title)}</Text>
      </View>
    ) : (
      <Text>Loading...</Text>
    )}
    </View>
  )
}

const styles = StyleSheet.create({
  mainView: {
    justifyContent: 'center',
    padding: 50,
  },
  textTittle: {
    fontSize: 35,
    marginBottom: 20,
  },
  textDescription: {
    fontSize: 20,
  }
})

export default DetailScreen
