import React, { useContext, useEffect, useState } from "react";
import {
 StyleSheet,
  Button,
  View,
  Image,
  Text,
  FlatList,
  TextInput,
  TouchableOpacity,
} from "react-native";
import {
  getCharacter,
  getNextCharacterPage,
  getCharacterId,
} from "../../component/api/rick-and-morty";
import UserScreen from "../userScreen";
import { stylesTheme } from "../../styles";
import { UserContext } from "../../contexts/userContext";

const CharDetailScreen = (props) => {
  const id = props.route.params.id;
  const [character, setCharacter] = useState({
    name: "",
    origin: {},
    location: {},
  });
  const navigation = props.navigation;
  useEffect(() => {
    buscar(id);
  }, []);
  async function buscar(id) {
    const c = await getCharacterId(id);
    setCharacter(c.data);
  }

  const { theme, setName } = useContext(UserContext)

  const componentStyle = theme ? stylesTheme[theme] : null;

  if (!componentStyle) {
    return null; 
  }

  return (
    <View style={[componentStyle.theme.mainView, styles.mainView]}>
      <UserScreen />
      <View style={styles.characterContainer}>
        <Image
          style={styles.characterImage}
          source={{ uri: character.image }}
        />
        <View>
          <Text style={componentStyle.theme.text}>{character.name}</Text>
          <Text style={componentStyle.theme.text}>{character.status}</Text>
        </View>
        <View>
          <Text style={componentStyle.theme.text}>{character.species}</Text>
          <Text style={componentStyle.theme.text}>{character.type}</Text>
          <Text style={componentStyle.theme.text}>{character.gender}</Text>
        </View>

        <TouchableOpacity
          onPress={() => {
            console.log("touchable opacity pressed");
            if (character.origin.url) {
              navigation.navigate("LocationDetail", {
                url: character.origin.url,
              });
            }
          }}
        >
          <View>
            <Text style={componentStyle.theme.text}>{character.origin.name}</Text>
          </View>
        </TouchableOpacity>

        <TouchableOpacity
          onPress={() => {
            console.log("touchable opacity pressed");
            if (character.location.url) {
              navigation.navigate("LocationDetail", {
                url: character.location.url,
              });
            }
          }}
        >
          <View>
            <Text style={componentStyle.theme.text}>{character.location.name}</Text>
          </View>
        </TouchableOpacity>
      </View>

      <Button
        color={componentStyle.buttonColor}
        onPress={() => {
          navigation.navigate("EpisodeDetail", { urls: character.episode });
        }}
        title="EPISODES"
      />
    </View>
  );
};

const styles = StyleSheet.create({
  mainView: {
    justifyContent: "center",
    padding: 50,
    backgroundColor: "white",
  },
  characterContainer: {
    flexDirection: "column",
    alignItems: "flex-start",
    margin: 5,
  },
  characterImage: {
    width: 90,
    height: 90,
    marginRight: 10,
  },
  textInput: {
    borderWidth: 1,
    borderColor: "#CCCCCC",
    height: 35,
    padding: 5,
  },
  marginVertical: {
    marginVertical: 5,
  },
});

export default CharDetailScreen;
