import { Button, StyleSheet, Text, View } from 'react-native';
import React, { useContext } from "react"
import { useState } from 'react';
import { useEffect } from 'react';
import stylesTheme from '../../styles';
import { UserContext } from '../../contexts/userContext';
import axios from 'axios';
import { Heart } from '@phosphor-icons/react';

const HomeScreen  = (props) => {

  const navigation = props.navigation

  const { theme, HandleSearch, HandleFavorite, characters } = useContext(UserContext)

  const componentStyle = theme ? stylesTheme[theme] : null;

  if (!componentStyle) {
    return null; 
  }

    return (
        <View style={[styles.mainView, componentStyle.theme.mainView]}>
          <Text style={[styles.textStyle, componentStyle.theme.text]}>Personagens de star wars</Text>
          <Button onPress={() => {return (navigation.navigate('settingScreen'))}} title='settings' />
          {characters ? (
            characters.map((person) => {
              return (
                <View key={person.created} style={styles.viewButton}>
                  <Button color={componentStyle.buttonColor} style={styles.containerCard} onPress={() => {return (HandleSearch(person.url), navigation.navigate('detailScreen'))}} title={person.name} />
                  <Button color={componentStyle.buttonColor} style={styles.viewButton} onPress={() => {return (HandleSearch(person.url), navigation.navigate('detailScreen'))}} title={<Heart size={32} />} />
                </View>
              );
            })
          ) : (
            <Text>Loading...</Text>
          )}
          </View>
        )
}

const styles = StyleSheet.create({
  mainView: {
    padding: 50,
    gap: 10,
  },
  textStyle: {
    fontSize: 35,
    color: 'black',
    marginBottom: 100,
  },
    containerCard: {
    cursor: 'pointer',
    padding: 20,
  },
  viewButton: {
    flex: 1,
    flexDirection: 'row',
    gap: 10,
  },
})


  export default HomeScreen
