import { StyleSheet, Text, View, TextInput, Button} from 'react-native';
import React, { useState, useContext } from "react"
import { UserContext } from '../../contexts/userContext';
import { stylesTheme } from '../../styles';

const LoginPage  = (props) => {
    const navigation = props.navigation
    const [user, setUser] = useState('')
    const [password, setPassword] = useState()
    const { theme, setName } = useContext(UserContext)

    const componentStyle = theme ? stylesTheme[theme] : null;

  if (!componentStyle) {
    return null; 
  }

    return (
        <View style={componentStyle.theme.mainView}>          
          <Text style={styles.textStyle}>User</Text>
          <TextInput
            value={user}
            onChangeText={(text) => setUser(text)}
            style={styles.input} />
          <Text style={componentStyle.theme.text}>Password</Text>
          <TextInput
            value={password}
            style={styles.input}
            secureTextEntry={true} />
          <Button
            title="LOGIN"
            style={componentStyle.theme.buttonTheme}
            onPress={() => {
              setName(user)
              navigation.navigate('Main')             
            }}/>
        </View>
      )
}


const styles = StyleSheet.create({
  mainView: {
    alignItems: 'center',
  },
  textStyle: {
    fontSize: 15,
    color: 'black'
  },
    input: {
    borderWidth: 1,
    borderRadius: 3,
    backgroundColor: 'white',
    height: 35,
    paddingHorizontal: 15,
    marginVertical: 10
  }
})


  export default LoginPage
